con:configuration.scriptText:
//storing last added customer details in Custom Property of testcase

import com.eviware.soapui.support.XmlHolder

def response = context.expand('${Add Customer#Response}')

log.info "$response"
def holder = new XmlHolder(response)
def customerId = holder.getNodeValue("//*:customerId")
context.testCase.setPropertyValue("Customer Id",customerId )

def FN = holder.getNodeValue("//*:firstname")
context.testCase.setPropertyValue("First Name", FN)

def LN = holder.getNodeValue("//*:lastname")
context.testCase.setPropertyValue("Last Name", LN)

def email = holder.getNodeValue("//*:email")
context.testCase.setPropertyValue("Email", email)

def telephone = holder.getNodeValue("//*:telephone")
context.testCase.setPropertyValue("Phone No", telephone)

def pin = holder.getNodeValue("//*:postcode")
context.testCase.setPropertyValue("Pincode", pin)

con:testCase.con:tearDownScript:
